";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"Messages:&nbsp;&nbsp;&nbsp;"&nbsp;.&nbsp;$status-&gt;messages&nbsp;&nbsp;&nbsp;&nbsp;.&nbsp;"&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"Recent:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"&nbsp;.&nbsp;$status-&gt;recent&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.&nbsp;"&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"Unseen:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"&nbsp;.&nbsp;$status-&gt;unseen&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.&nbsp;"&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"UIDnext:&nbsp;&nbsp;&nbsp;&nbsp;"&nbsp;.&nbsp;$status-&gt;uidnext&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.&nbsp;"&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"UIDvalidity:"&nbsp;.&nbsp;$status-&gt;uidvalidity&nbsp;.&nbsp;"&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(imap_renamemailbox($mbox,&nbsp;"{imap.example.org}INBOX.$newname",&nbsp;"{imap.example.org}INBOX.$name2"))&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"renamed&nbsp;new&nbsp;mailbox&nbsp;from&nbsp;'$name1'&nbsp;to&nbsp;'$name2'&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$newname&nbsp;=&nbsp;$name2;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;else&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"imap_renamemailbox&nbsp;on&nbsp;new&nbsp;mailbox&nbsp;failed:&nbsp;"&nbsp;.&nbsp;imap_last_error()&nbsp;.&nbsp;"&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;else&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"imap_status&nbsp;on&nbsp;new&nbsp;mailbox&nbsp;failed:&nbsp;"&nbsp;.&nbsp;imap_last_error()&nbsp;.&nbsp;"&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(@imap_deletemailbox($mbox,&nbsp;"{imap.example.org}INBOX.$newname"))&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"new&nbsp;mailbox&nbsp;removed&nbsp;to&nbsp;restore&nbsp;initial&nbsp;state&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;else&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"imap_deletemailbox&nbsp;on&nbsp;new&nbsp;mailbox&nbsp;failed:&nbsp;"&nbsp;.&nbsp;implode("&lt;br&nbsp;/&gt;
",&nbsp;imap_errors())&nbsp;.&nbsp;"&lt;br&nbsp;/&gt;
";&nbsp;&nbsp;&nbsp;&nbsp;}}&nbsp;else&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;"could&nbsp;not&nbsp;create&nbsp;new&nbsp;mailbox:&nbsp;"&nbsp;.&nbsp;implode("&lt;br&nbsp;/&gt;
",&nbsp;imap_errors())&nbsp;.&nbsp;"&lt;br&nbsp;/&gt;
";}imap_close($mbox);?&gt;                       See Also            imap_renamemailbox() - Rename an old mailbox to new mailbox     imap_deletemailbox() - Delete a mailbox

